mapper.select:
select t.rnum , t.qid , t.qtypenm , t.question , t.url , t.writernm , t.createdt , t.admnum , t.useyn from ( select r_tbl.rnum , r_tbl.qid , r_tbl.qtypenm , r_tbl.question as question , r_tbl.url , r_tbl.writernm , r_tbl.createdt , r_tbl.admnum , r_tbl.useyn from (select row_number() over (order by a.qid desc) rnum , a.qid , b.nm as qtypenm , a.question , a.url , c.name as writernm , date_format(STR_TO_DATE(a.createdt, '%Y%m%d%H%i%S'), '%Y-%m-%d') as createdt , a.admnum , a.useyn from studydb.question a , studydb.quetype b , studydb.users c where a.qtypecd = b.qtypecd and a.writer = c.sabun and a.QTYPECD like #{authDetail} and (select authcd from ratio where QTYPECD = a.QTYPECD) like #{auth} and a.CREATEDT between #{staymd} and #{endymd}
and ${searchItem} = #{searchInput}
) r_tbl ) t

mapper.insert:
INSERT INTO studydb.question(qid, qtypecd, question, anstype, writer, createdt, lvl, useyn, modifyuser, admnum,
                             explanation)
    VALUE (#{qid},#{qtypecd},#{question},'M','99999999',#{createdt},#{lvl},'Y','99999999','70', #{explanation});

mapper.update:
UPDATE question
   SET LVL = #{lvl}
     , QUESTION = #{question}
     , EXPLANATION = #{explanation}
 WHERE QID = #{qid}

UPDATE queitem
SET ITEM = #{item1}


        ,ANSCD = '0'


        ,ANSCD = null


WHERE ITEMID = '1'
  AND QID = #{qid}

UPDATE queitem
SET ITEM = #{item2}


        ,ANSCD = '0'


        ,ANSCD = null


WHERE ITEMID = '2'
AND QID = #{qid}

UPDATE queitem
SET ITEM = #{item3}


        ,ANSCD = '0'


        ,ANSCD = null


WHERE ITEMID = '3'
AND QID = #{qid}

UPDATE queitem
SET ITEM = #{item4}


        ,ANSCD = '0'


        ,ANSCD = null


WHERE ITEMID = '4'
AND QID = #{qid}
